// Boolen to track if error found  
var foundErr;
// Form element index number which the first error occured. 
var focusOn;

function check_form(f) {
  var message = "";
  foundErr = false;
  focusOn = -1;
  // fields should not be empty and should contain numbers only
  if (bis_query.oth_nos.disabled == false) {
    if (bis_query.oth_nos.value.length == 0) {
      message = "\r\nPlease enter House Bill or Resolution to search for.";
      foundErr = true;
      focusOn = 0;
    }
    //Has any error occured? 
    if (foundErr) {
      //Yes. Focus on which the first occurred. 
      alert("Nothing to SEARCH:\r\n" + message);
      bis_query.oth_nos.focus(focusOn);
    } else {
      // No. Submit the form. 
      bis_query.submit();
    }
  } else {
    bis_query.submit();
  }
}
